mapper.sql:
from post_record F, (select C.id,E.user_name,C.reply_time from (select Max(B.reply_time) reply_time,A.id from post_record A left join reply B on A.id = B.post_id group by A.id)C left join reply D on (C.id = D.post_id and C.reply_time = D.reply_time) left join user E on E.id = D.replier_id) G,user H where F.id = G.id and F.author_id = H.id
and F.title like #{title}
and F.section_id = 1
